import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchPatientName, fetchTreatments, fetchInjuries } from '../api/fetchPatientDetails';
import { updateInjuryStatus } from '../api/updateInjuryStatus';

const Treatment = () => {
  const { patientId } = useParams();
  const navigate = useNavigate();
  const [patientName, setPatientName] = useState(''); // 患者名を追加
  const [treatments, setTreatments] = useState([]); // eslint-disable-next-line no-unused-vars
  const [injuries, setInjuries] = useState([]);

  useEffect(() => {
    const getPatientDetails = async () => {
      try {
        const name = await fetchPatientName(patientId);
        setPatientName(name);

        const treatmentData = await fetchTreatments(patientId);
        setTreatments(treatmentData);

        const injuryData = await fetchInjuries(patientId);
        setInjuries(injuryData);
      } catch (error) {
        console.error('患者情報の取得に失敗しました:', error.message);
      }
    };
    getPatientDetails();
  }, [patientId]);

  const handleCureToggle = async (injuryId, isHealed) => {
    try {
      const updated = await updateInjuryStatus(injuryId, isHealed);
      if (updated) {
        const updatedInjuries = await fetchInjuries(patientId);
        setInjuries(updatedInjuries);
        alert(isHealed ? '完治に更新しました！' : '治療中に戻しました！');
      } else {
        alert('更新に失敗しました。');
      }
    } catch (error) {
      console.error('治癒更新中にエラーが発生:', error.message);
    }
  };

  return (
    <div>
      <h2>患者詳細ページ</h2>
      <h3>患者名: {patientName}</h3> 
      <button
        onClick={() => navigate('/')}
        style={{
          marginBottom: '10px',
          padding: '8px 16px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        トップページに戻る
      </button>

      {/* 受傷履歴 */}
      <div>
        <h3>受傷履歴</h3>
        {injuries.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>日付</th>
                <th>部位</th>
                <th>痛みレベル</th>
                <th>治癒状態</th>
                <th>操作</th>
              </tr>
            </thead>
            <tbody>
              {injuries.map((injury) => (
                <tr key={injury.id}>
                  <td>{injury.date}</td>
                  <td>{injury.body_part}</td>
                  <td>{injury.pain_level}</td>
                  <td>{injury.is_healed ? '完治' : '治療中'}</td>
                  <td>
                    {injury.is_healed ? (
                      <button onClick={() => handleCureToggle(injury.id, false)}>
                        完治の取り消し
                      </button>
                    ) : (
                      <button onClick={() => handleCureToggle(injury.id, true)}>
                        治癒
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>受傷履歴がありません。</p>
        )}
      </div>

      {/* 施術履歴 */}
      <div style={{ marginTop: '20px' }}>
        <h3>施術履歴</h3>
        {treatments.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>日付</th>
                <th>内容</th>
                <th>部位</th>
              </tr>
            </thead>
            <tbody>
              {treatments.map((treatment) => (
                <tr key={treatment.id}>
                  <td>{treatment.date}</td>
                  <td>{treatment.content}</td>
                  <td>{treatment.body_part}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>施術履歴がありません。</p>
        )}
      </div>
    </div>
  );
};

export default Treatment;
