import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchTreatments, fetchInjuries } from '../api/fetchPatientDetails';

const Treatment = () => {
  const { patientId } = useParams();
  const [treatments, setTreatments] = useState([]);
  const [injuries, setInjuries] = useState([]);

  useEffect(() => {
    const getPatientDetails = async () => {
      const treatmentData = await fetchTreatments(patientId);
      const injuryData = await fetchInjuries(patientId);
      setTreatments(treatmentData);
      setInjuries(injuryData);
    };
    getPatientDetails();
  }, [patientId]);

  return (
    <div>
      <h2>患者詳細ページ</h2>
      <h3>施術履歴</h3>
      {treatments.length > 0 ? (
        <ul>
          {treatments.map((treatment) => (
            <li key={treatment.id}>
              {treatment.date} - {treatment.content} - 部位: {treatment.body_part}
            </li>
          ))}
        </ul>
      ) : (
        <p>施術履歴がありません。</p>
      )}

      <h3>受傷履歴</h3>
      {injuries.length > 0 ? (
        <ul>
          {injuries.map((injury) => (
            <li key={injury.id}>
              {injury.date} - {injury.body_part} - 痛みレベル: {injury.pain_level} - 治癒: {injury.is_healed ? '完治' : '治療中'}
            </li>
          ))}
        </ul>
      ) : (
        <p>受傷履歴がありません。</p>
      )}
    </div>
  );
};

export default Treatment;
