import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchTreatments, fetchInjuries } from '../api/fetchPatientDetails';
import { updateInjuryStatus } from '../api/updateInjuryStatus';

const Treatment = () => {
  const { patientId } = useParams();
  // eslint-disable-next-line no-unused-vars
  const [treatments, setTreatments] = useState([]);
  const [injuries, setInjuries] = useState([]);
  const [activeTab, setActiveTab] = useState('treatments');

  useEffect(() => {
    const getPatientDetails = async () => {
      const treatmentData = await fetchTreatments(patientId);
      const injuryData = await fetchInjuries(patientId);
      setTreatments(treatmentData);
      setInjuries(injuryData);
    };
    getPatientDetails();
  }, [patientId]);

  const toggleTab = (tabName) => {
    setActiveTab(tabName);
  };

  const handleCureToggle = async (injuryId, isHealed) => {
    try {
      const updated = await updateInjuryStatus(injuryId, isHealed);
      if (updated) {
        console.log('治癒状態更新成功:', updated);
        const updatedInjuries = await fetchInjuries(patientId);
        setInjuries(updatedInjuries);
        alert(isHealed ? '完治に更新しました！' : '治療中に戻しました！');
      } else {
        alert('更新に失敗しました。');
      }
    } catch (error) {
      console.error('治癒更新中にエラーが発生:', error.message);
    }
  };

  return (
    <div>
      <h2>患者詳細ページ</h2>

      <div>
        <button onClick={() => toggleTab('treatments')}>施術履歴</button>
        <button onClick={() => toggleTab('injuries')}>受傷履歴</button>
      </div>

      {activeTab === 'injuries' && (
        <div>
          <h3>受傷履歴</h3>
          {injuries.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>日付</th>
                  <th>部位</th>
                  <th>痛みレベル</th>
                  <th>治癒状態</th>
                  <th>操作</th>
                </tr>
              </thead>
              <tbody>
                {injuries.map((injury) => (
                  <tr key={injury.id}>
                    <td>{injury.date}</td>
                    <td>{injury.body_part}</td>
                    <td>{injury.pain_level}</td>
                    <td>{injury.is_healed ? '完治' : '治療中'}</td>
                    <td>
                      {injury.is_healed ? (
                        <button onClick={() => handleCureToggle(injury.id, false)}>
                          完治の取り消し
                        </button>
                      ) : (
                        <button onClick={() => handleCureToggle(injury.id, true)}>
                          治癒
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>受傷履歴がありません。</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Treatment;
