// eslint-disable-next-line no-unused-vars
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
// eslint-disable-next-line no-unused-vars
import { fetchPatientName, fetchTreatments, fetchInjuries } from '../api/fetchPatientDetails';
import { addPatient, updatePatient, deletePatient } from '../api/patientOperations';

const Treatment = () => {
  const { patientId } = useParams();
  const navigate = useNavigate();
  const [patientName, setPatientName] = useState('');
  const [editName, setEditName] = useState('');
  const [editTeam, setEditTeam] = useState('');
  const [isNew, setIsNew] = useState(false);
  const [isEditing, setIsEditing] = useState(false); // 編集モード管理

  useEffect(() => {
    if (patientId === 'new') {
      setIsNew(true);
      setIsEditing(true); // 新規作成時は編集モードON
      return;
    }

    const getPatientDetails = async () => {
      const name = await fetchPatientName(patientId);
      setPatientName(name);
      setEditName(name); // 編集用フィールドに名前をセット
    };
    getPatientDetails();
  }, [patientId]);

  // 患者を追加または更新
  const handleSave = async () => {
    if (isNew) {
      await addPatient(editName, editTeam);
      alert('患者を追加しました！');
    } else {
      await updatePatient(patientId, editName, editTeam);
      alert('患者情報を更新しました！');
    }
    setIsEditing(false); // 編集モードをOFF
    navigate('/patients');
  };

  // 患者を削除
  const handleDelete = async () => {
    if (window.confirm('本当に削除しますか？')) {
      await deletePatient(patientId);
      alert('患者を削除しました！');
      navigate('/patients');
    }
  };

  return (
    <div>
      <h2>患者詳細ページ</h2>

      <button onClick={() => navigate('/')}>
        トップページに戻る
      </button>

      <div>
        <h3>{isNew ? '患者新規作成' : `患者名: ${patientName}`}</h3>

        {/* 編集ボタン */}
        {!isEditing && (
          <button onClick={() => setIsEditing(true)} style={{ marginBottom: '10px' }}>
            編集
          </button>
        )}

        {/* 入力フィールド（編集モードのみ表示） */}
        {isEditing && (
          <>
            <input
              placeholder="患者名"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
            />
            <input
              placeholder="チーム"
              value={editTeam}
              onChange={(e) => setEditTeam(e.target.value)}
            />
            <button onClick={handleSave}>{isNew ? '追加' : '保存'}</button>
          </>
        )}

        {/* 削除ボタン */}
        {!isNew && (
          <button onClick={handleDelete} style={{ marginLeft: '10px', color: 'red' }}>
            削除
          </button>
        )}
      </div>
    </div>
  );
};

export default Treatment;
